import { createSlice } from "@reduxjs/toolkit";

CouterSlide = createSlice({
    name: "counter",
    initialState: 0,
    reducers: {
        increment: (state) => state + 1,
        decrement: (state) => state - 1,
    },
});

export const { increment, decrement } = CouterSlide.actions;
export default CouterSlide.reducer;
