import { createSlice } from "@reduxjs/toolkit";

const CouterSlide = createSlice({
    name: "counter",
    initialState: 0,
    reducers: {
        increment: (state) => ({
            ...state,
            count: state.count + 1,
        }),
        decrement: (state) => ({
            ...state,
            count: state.count - 1,
        }),
    },
});

export const { increment, decrement } = CouterSlide.actions;
export default CouterSlide.reducer;
