import { createSlice } from "@reduxjs/toolkit";

export default createSlice({
    name: "counter",
    initialState: {
        count: 0,
    },
    reducers: {
        increment: (state) => ({
            ...state,
            count: state.count + 1,
        }),
        decrement: (state) => ({
            ...state,
            count: state.count - 1,
        }),
    },
});
