const INCREMENT = "increment";
const DECREMENT = "decrement";

const initialState = {
    count: 0,
};
export const increment = () => ({
    type: INCREMENT,
});
export const decrement = () => ({
    type: DECREMENT,
});

function counterReducer(state = initialState, action) {
    switch (action.type) {
        case "increment":
            return {
                ...state,
                count: state.count + 1,
            };
        case "decrement":
            return {
                ...state,
                count: state.count - 1,
            };
        default:
            return state;
    }
}
export default counterReducer;
